const base_url = "http://localhost:8080/api/v1";

export function getLeader(saintId) {
    const path = base_url + "/leader" + "?saintId=" + saintId;
    return getMethod(getPath("/leader"), getHeader);
}

function getMethod(url) {
    return fetch(url, {
        headers: header
    })
}

const getHeader = {
